import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import frame1 from "../images/Before_Jonathan.png";
import frame2 from "../images/Suggest_Jonathan.png";
import frame3 from "../images/After_Jonathan.png";


function Bored() {
    
    const[activity, setActivity] = React.useState("")
    
    return (
        <div className="">            
            <h3>Big Block Image (Bored)</h3>
            <ImageText activitySetter={setActivity}/>
            <ImageItem activityText={activity}/>
        </div>
    )
}//end method

function ImageText(props) {
    
    
    if (props.activity != null) {
        console.log(props.activity)
    }//end if
    
    
    
    function getActivity() {        
        fetch(`http://www.boredapi.com/api/activity/`)
        .then(res => res.json())
        .then(data => props.activitySetter(data))
    }//end method
    
    
    React.useEffect(
        getActivity,
        []        
    )   
    
    return (
        <div className="">
            {props != null && <h3>{props.activity}</h3>}
        </div>
    )
}//end method

function ImageItem(props) {

    const responses = ["Great idea!", "Excellent idea!", "I'll do that now!", "Heck yes!"]
    const id = Math.floor(Math.random() * responses.length)
    let activity = ""
    if (props.activityText != "") {
        activity = props.activityText.activity
        activity = activity.toLowerCase()
        activity = `I'm bored, should I ${activity}?`
    }
    

    
    return(
        <div className="">
            <div className="image-container">
                <img src={frame1} alt="Test1" />
                {props.activityText != "" && <h6>{activity}</h6>}
            </div>
            <div className="image-container">
                <img src={frame2} alt="Test2" />
                <h6>No! You should hire Jonathan Pruitt!</h6>
            </div>
            <div className="image-container">
                <img src={frame3} alt="Test3" />
                <h6>{responses[id]}</h6>
                
            </div>
        </div>
    )

}//end method

export default Bored;
