import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import frame1 from "../images/Before_Jonathan.png";
import frame2 from "../images/Suggesting_Jonathan.png";
import frame3 from "../images/After_Jonathan.png";


function Bored() {

    return (
        <div className="">            
            <h3>Big Block Image (Bored)</h3>
            <ImageText />
            <ImageItem />
        </div>
    )
}//end method

function ImageText() {
    
    const[activity, setActivity] = React.useState()
           
    if (activity != null) {
        console.log(activity)
    }//end if
    
    
    
    function getActivity() {        
        fetch(`http://www.boredapi.com/api/activity/`)
            .then(res => res.json())
            .then(data => setActivity(data))
    }//end method
    
    
    React.useEffect(
        getActivity,
        []        
    )   

    return (
        <div className="">
            {activity != null && <h3>{activity.activity}</h3>}
        </div>
    )
}//end method

